mapper.select:
# 1대1 게시판 조회
SELECT P.POST_ID -- 포스트 ID , P.BOARD_ID -- 보드 ID , P.MEMBER_ID -- 맴버 ID , P.POST_TYPE -- 게시 유형 , P.POST_STATUS -- 게시글 상태 , P.POST_TITLE -- 게시글 제목 , P.POST_CONTENT -- 게시글 내용 , DATE_FORMAT(P.INPUT_DT, '%Y-%m-%d') AS INPUT_DT -- 작성일 , M.USER_NAME -- 작성자 FROM POSTS P -- 게시글테이블 LEFT JOIN MEMBERS M ON P.MEMBER_ID = M.MEMBER_ID -- 멤버테이블 WHERE 1=1 AND BOARD_ID = 5 -- 조건1.넥사에서 받을 START_DATE AND P.INPUT_DT >= STR_TO_DATE(#{START_DATE}, '%Y%m%d%H%i%s')
-- 조건2.넥사에서 받을 END_DATE AND P.INPUT_DT <= STR_TO_DATE(#{END_DATE}, '%Y%m%d%H%i%s')
-- 조건3.넥사에서 받을 USER_NAME AND M.USER_NAME LIKE CONCAT('%', #{USER_NAME}, '%')
-- 조건4.넥사에서 받을 POST_STATUS AND P.POST_STATUS = #{POST_STATUS}
-- 조건5.넥사에서 받을 POST_ID AND P.POST_ID = #{POST_ID}
