mapper.insert:
		INSERT INTO MEMBERS(
        	MEMBER_ID, 
        	USER_NAME, 
        	PASSWORD, 
        	EMAIL_ADDR, 
        	PHONE_NUMBER,
            BIRTH, 
            GENDER, 
            GRADE_CODE, 
            MEMBER_TYPE, 
            LOGIN_TYPE
  	   )VALUES(
       		#{MEMBER_ID}
        	,#{USER_NAME}
        	,#{PASSWORD}
        	,#{EMAIL_ADDR}
        	,REPLACE(REPLACE(#{PHONE_NUMBER}, '-', ''), ' ', '') 
        	,#{BIRTH}
        	,#{GENDER}
        	,#{GRADE_CODE}
        	,#{MEMBER_TYPE}
        	,CASE 
            	WHEN #{GRADE_CODE} = 9 THEN 'NORMAL'
            	ELSE #{LOGIN_TYPE}
        	  END
    	 )

mapper.select:
SELECT COUNT(*)
  FROM MEMBERS
 WHERE (MEMBER_ID = #{MEMBER_ID}
 	OR EMAIL_ADDR = #{EMAIL_ADDR}
 	OR PHONE_NUMBER = #{PHONE_NUMBER})

mapper.update:
UPDATE MEMBERS
   SET USER_NAME = #{USER_NAME}
   	   ,PASSWORD = #{PASSWORD}
   	   ,EMAIL_ADDR = #{EMAIL_ADDR}
   	   ,PHONE_NUMBER = #{PHONE_NUMBER}
   	   ,GENDER = #{GENDER}
   	   ,BIRTH = STR_TO_DATE(#{BIRTH}, '%Y%m%d%H%i%s')
   	   ,FIRST_LOGIN_DT = STR_TO_DATE(#{FIRST_LOGIN_DT}, '%Y%m%d%H%i%s')
   	   ,LAST_LOGIN_DT = STR_TO_DATE(#{LAST_LOGIN_DT}, '%Y%m%d%H%i%s')
   	   ,GRADE_CODE = #{GRADE_CODE}
   	   ,MEMBER_TYPE = #{MEMBER_TYPE}
   	   ,STATUS = #{STATUS}
   	   ,UPDATE_DT = NOW()
 WHERE MEMBER_ID = #{MEMBER_ID}

mapper.select:
SELECT COUNT(*)
  FROM MEMBERS
 WHERE MEMBER_ID != #{MEMBER_ID}
   AND (EMAIL_ADDR = #{EMAIL_ADDR}
    OR PHONE_NUMBER = #{PHONE_NUMBER})

mapper.update:
UPDATE MEMBERS M SET M.GRADE_CODE =
#{GRADE_CODE}
COALESCE( (SELECT G.GRADE_CODE FROM GRADES G WHERE M.PRICE_AMOUNT >= G.STANDARD_AMOUNT AND G.GRADE_CODE != 9 ORDER BY G.STANDARD_AMOUNT DESC LIMIT 1), 1 )
WHERE M.MEMBER_ID = #{MEMBER_ID}

mapper.select:
SELECT P.MEMBER_ID ,P.CHANGE_TYPE ,P.POINT ,P.DESCRIPTION ,P.INPUT_DT ,COALESCE(O.ORDER_NUMBER, '관리자 직접 처리') AS ORDER_NUMBER FROM POINTS P LEFT JOIN ORDERS O ON P.ORDER_ID = O.ORDER_ID WHERE P.MEMBER_ID = #{MEMBER_ID}
AND P.CHANGE_TYPE = #{CHANGE_TYPE}
AND P.INPUT_DT BETWEEN #{SDATE} AND DATE_ADD(#{EDATE}, INTERVAL 1 DAY)
AND COALESCE(O.ORDER_NUMBER, '관리자 직접 처리') LIKE CONCAT('%',TRIM(#{ORDER_NUMBER}),'%')
ORDER BY P.INPUT_DT DESC
