mapper.select:
#안읽은 메세지 개수 처리
	SELECT COUNT(*) 
	  FROM TB_NOTIFICATION 
	 WHERE RECEIVER_ID = #{userId}
	   AND RECEIVER_TYPE = #{receiverType}
	   AND READ_YN = 'N'

SELECT NOTI_MESSAGE
     , NOTI_ID
  FROM TB_NOTIFICATION
 WHERE RECEIVER_ID = #{userId}
   AND RECEIVER_TYPE = #{receiverType}
   AND READ_YN = 'N'

mapper.update:
UPDATE TB_NOTIFICATION
   SET READ_YN = 'Y'
 WHERE RECEIVER_ID = #{userId}
   AND NOTI_ID = #{notiId}
