mapper.select:
SELECT p.PROMOTION_ID ,p.PROMOTION_NAME ,p.PROMOTION_TYPE ,p.DESCRIPTION ,p.START_DT ,p.END_DT ,p.IS_ACTIVE ,p.INPUT_DT ,p.UPDATE_DT ,p.INPUT_ID ,p.UPDATE_ID ,p.AUTO_ISSUED ,pc.P_COUPON_CODE ,pc.ISSUED_DT ,pc.EXPIRY_DT ,pc.IS_USED ,pd.DISCOUNT_ID ,pd.DISCOUNT_TYPE ,pd.DISCOUNT_VALUE ,pd.MIN_ORDER_PRICE ,pt.TARGET_ID ,pt.TARGET_TYPE ,pt.TARGET_VALUE ,pt.TARGET_AMOUNT FROM PROMOTIONS p LEFT JOIN PROMOTION_COUPON pc ON p.PROMOTION_ID = pc.PROMOTION_ID LEFT JOIN PROMOTION_DISCOUNT pd ON p.PROMOTION_ID = pd.PROMOTION_ID LEFT JOIN PROMOTION_TARGET pt ON p.PROMOTION_ID = pt.PROMOTION_ID
AND p.PROMOTION_NAME LIKE CONCAT('%', #{promotionName}, '%')
AND p.PROMOTION_TYPE = #{promotionType}
AND pt.TARGET_VALUE = #{targetValue}
AND NOW() BETWEEN p.START_DT AND p.END_DT
AND (NOW() < p.START_DT OR NOW() > p.END_DT)
AND p.IS_ACTIVE = #{isActive}
AND p.START_DT >= #{startDate}
AND p.END_DT <= #{endDate}
ORDER BY p.PROMOTION_ID DESC

mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO PROMOTIONS(
    PROMOTION_NAME
    ,PROMOTION_TYPE
    ,DESCRIPTION
    ,START_DT
    ,END_DT
    ,IS_ACTIVE
    ,AUTO_ISSUED
    ,INPUT_DT
    ,UPDATE_DT
    ,INPUT_ID
    ,UPDATE_ID
)VALUES(
    #{PROMOTION_NAME}
    ,#{PROMOTION_TYPE}
    ,#{DESCRIPTION}
    ,#{START_DT}
    ,#{END_DT}
    ,#{IS_ACTIVE}
    ,#{AUTO_ISSUED} --신규추가함
    ,NOW()
    ,NOW()
    ,#{INPUT_ID}
    ,#{INPUT_ID}
)

mapper.update:
UPDATE PROMOTIONS SET
     PROMOTION_NAME = #{PROMOTION_NAME}
    ,PROMOTION_TYPE = #{PROMOTION_TYPE}
    ,DESCRIPTION = #{DESCRIPTION}
    ,START_DT = #{START_DT}
    ,END_DT = #{END_DT}
    ,IS_ACTIVE = #{IS_ACTIVE}
    ,AUTO_ISSUED = #{AUTO_ISSUED}
    ,UPDATE_DT = NOW()
    ,UPDATE_ID = #{UPDATE_ID}
WHERE PROMOTION_ID = #{PROMOTION_ID}

UPDATE PROMOTION_DISCOUNT SET
     DISCOUNT_TYPE = #{DISCOUNT_TYPE}
    ,DISCOUNT_VALUE = #{DISCOUNT_VALUE}
    ,MIN_ORDER_PRICE = #{MIN_ORDER_PRICE}
    ,UPDATE_DT = NOW()
    ,UPDATE_ID = #{UPDATE_ID}
WHERE PROMOTION_ID = #{PROMOTION_ID}

UPDATE PROMOTION_TARGET SET
     TARGET_TYPE = #{TARGET_TYPE}
    ,TARGET_VALUE = #{TARGET_VALUE}
    ,TARGET_AMOUNT = #{TARGET_AMOUNT}
    ,UPDATE_DT = NOW()
    ,UPDATE_ID = #{UPDATE_ID}
WHERE PROMOTION_ID = #{PROMOTION_ID}

UPDATE PROMOTION_COUPON SET
     P_COUPON_CODE = #{P_COUPON_CODE}
    ,EXPIRY_DT = #{EXPIRY_DT}
    ,PERIOD_SETTING = #{PERIOD_SETTING}
WHERE PROMOTION_ID = #{PROMOTION_ID}
