SELECT MEMBER_ID, "O" as PASSWORD
FROM   MEMBERS
WHERE  MEMBER_ID = #{id}
  AND  PASSWORD = #{password}

SELECT COUNT(MEMBER_ID) AS COUNT
FROM MEMBERS
WHERE MEMBER_ID = #{id}

SELECT COUNT(MEMBER_ID) AS COUNT
FROM MEMBERS
WHERE EMAIL_ADDR = #{to}

SELECT MEMBER_ID
FROM MEMBERS
WHERE EMAIL_ADDR = #{to}
