mapper.select:
SELECT P.PRODUCT_ID ,P.PRODUCT_CODE ,P.PRODUCT_NAME ,P.PRODUCT_PRICE ,P.COST_PRICE ,P.SALED_PRICE ,P.PRODUCT_CONTENT ,P.PRODUCT_WEIGHT ,P.IS_VISIBLE ,P.SORT_NUMBER ,COALESCE(GROUP_CONCAT(O.OPTION_NAME, ':', O.OPTION_VALUE SEPARATOR ','), '') AS OPTIONS ,COALESCE(COUNT(R.REVIEW_ID), 0) AS REVIEW_COUNT ,COALESCE(AVG(R.STAR_POINT), 0) AS AVG_STAR_POINT FROM PRODUCTS P LEFT JOIN OPTIONS O ON P.PRODUCT_ID = O.PRODUCT_ID LEFT JOIN REVIEWS R ON P.PRODUCT_ID = R.PRODUCT_ID AND R.REVIEW_STATUS = '정상' WHERE P.IS_VISIBLE = 'Y'
AND P.SORT_NUMBER = #{sortNumber}
GROUP BY P.PRODUCT_ID ,P.PRODUCT_CODE ,P.PRODUCT_NAME ,P.PRODUCT_PRICE ,P.COST_PRICE ,P.SALED_PRICE ,P.PRODUCT_CONTENT ,P.PRODUCT_WEIGHT ,P.IS_VISIBLE ,P.SORT_NUMBER ORDER BY P.SORT_NUMBER ASC
